#ifndef __Camel_Base_System_SystemErrorConsts__
#define __Camel_Base_System_SystemErrorConsts__

// Error type
#define CAMEL_BASE_ERROR			0x03000000

// Error categories
#define CAMEL_COMMON_ERROR				(CAMEL_BASE_ERROR | 0x00000000)
#define CAMEL_SYSTEM_ERROR				(CAMEL_BASE_ERROR | 0x00010000)
#define CAMEL_NETWORK_ERROR				(CAMEL_BASE_ERROR | 0x00020000)
#define CAMEL_DATABASE_ERROR			(CAMEL_BASE_ERROR | 0x00040000)
#define CAMEL_LOGGER_ERROR				(CAMEL_BASE_ERROR | 0x00080000)

// Common
#define CAMEL_SUCCESS					(CAMEL_COMMON_ERROR & 0x00000000)
#define CAMEL_FAILED					(CAMEL_COMMON_ERROR | 0xFFFFFFFF)
#define CAMEL_CREATE_FAILED				(CAMEL_COMMON_ERROR | 0x00000001)
#define CAMEL_INVALID_PARAMETER			(CAMEL_COMMON_ERROR | 0x00000002)
#define CAMEL_NOT_ENOUGH_MEMORY			(CAMEL_COMMON_ERROR | 0x00000003)
#define CAMEL_INVALID_EVENT_CALLBACK	(CAMEL_COMMON_ERROR | 0x00000004)
#define CAMEL_INVALID_HANDLE			(CAMEL_COMMON_ERROR | 0x00000005)
#define CAMEL_DUPLICATED				(CAMEL_COMMON_ERROR | 0x00000006)
#define CAMEL_NOT_FOUND_KEY				(CAMEL_COMMON_ERROR | 0x00000007)
#define CAMEL_NOT_IMPLEMENTATION		(CAMEL_COMMON_ERROR | 0x00000008)

// Socket
#define CAMEL_INVALID_SOCKET			(CAMEL_NETWORK_ERROR | 0x00000000)
#define CAMEL_CONNECT_FAILED			(CAMEL_NETWORK_ERROR | 0x00000001)
#define CAMEL_CONNECT_TIMEOUT			(CAMEL_NETWORK_ERROR | 0x00000002)
#define CAMEL_SOCKET_BINDING_ERROR		(CAMEL_NETWORK_ERROR | 0x00000003)
#define CAMEL_SOCKET_CLOSED				(CAMEL_NETWORK_ERROR | 0x00000004)
#define CAMEL_IO_PENDING_FAILED			(CAMEL_NETWORK_ERROR | 0x00000005)
#define CAMEL_INTERRUPTED				(CAMEL_NETWORK_ERROR | 0x00000006)
#define CAMEL_SETSOCKOPT_FAILED			(CAMEL_NETWORK_ERROR | 0x00000007)
#define CAMEL_IO_ICMP_PORTUNREACHABLE	(CAMEL_NETWORK_ERROR | 0x00000008)
#define CAMEL_RECV_TIMEOUT				(CAMEL_NETWORK_ERROR | 0X00000009)
#define CAMEL_NOT_ENOUGH_ASYNCUSERBUFFER	(CAMEL_NETWORK_ERROR | 0X0000000A)

// Database
#define CAMEL_CONNECT_DB_FAILED			(CAMEL_DATABASE_ERROR | 0x00000000)
#define CAMEL_NOT_ENOUGH_DB_POOL		(CAMEL_DATABASE_ERROR | 0x00000001)


#endif // __Camel_Base_System_SystemErrorConsts__